import React, {useRef, useState} from 'react';
import './styles/App.css';
import TaskItem from "./components/TaskItem";
import TaskList from "./components/TaskList";
import MyButton from "./components/UI/button/MyButton";
import MyInput from "./components/UI/input/MyInput";

function App() {
    const [tasks, setTasks] = useState([
        {id: 1, title: 'Title', body: "Description"},
        {id: 2, title: 'Title', body: "Description"},
        {id: 3, title: 'Title', body: "Description"},
    ]);
    const [title, setTitle] = useState('');
    const bodyInputRef = useRef();

    const addNewTask = (e) => {
        e.preventDefault()
    }

  return (
    <div className="App">
        <form>
            {/*Управляемый компонент*/}
            <MyInput
                value={title}
                onChange={e => setTitle(e.target.value)}
                type="text"
                placeholder="Название задачи"
            />
            <MyInput
                ref={bodyInputRef}
                type="text"
                placeholder="Описание задачи"
            />
            <MyInput type="text" placeholder="Категория"/>
            <MyInput type="text" placeholder="Сотрудник"/>
            <MyButton onClick={addNewTask}>Создать задачу</MyButton>
        </form>
        <TaskList tasks={tasks}/>
    </div>
  );
}

export default App;
