import React, {useState} from 'react';
import Header from "./Header";
import MyInput from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";
import UserService from "../API/UserService";

const SignUpEmployee = () => {
    const [employee, setEmployee] = useState({name: '', password: ''});
    const [message, setMessage] = useState(false)

    const addNewEmployee = (e) => {
        e.preventDefault()
        const newEmployee = {
            ...employee, role: "EMPLOYEE"
        }
        signUpEmployee(newEmployee)
        setEmployee({name: '',
            password: ''})
        setMessage(true);
    }

    const signUpEmployee = async (newEmployee) => {
        await UserService.create(newEmployee);
    }

    return (
        <div>
            <Header/>
            <h1 style={{textAlign: "center"}}>Регистрация нового сотрудника</h1>
            if (message)
            <isClick message={message}/>
            <form>
                {/*Управляемый компонент*/}
                <MyInput
                    value={employee.name}
                    onChange={e => setEmployee({...employee, name: e.target.value})}
                    type="text"
                    placeholder="Имя"
                />
                <MyInput
                    value={employee.password}
                    onChange={e => setEmployee({...employee, password: e.target.value})}
                    type="text"
                    placeholder="Пароль"
                />
                <MyButton onClick={addNewEmployee}>Зарегистрировать</MyButton>
            </form>
        </div>
    );
};

function isClick(props) {
    const isClick = props.message;

    if (isClick) {
        return <h2 style={{color: "green"}}>Операция прошла успешно</h2>
    } else {
        return <h2> </h2>;
    }
}

export default SignUpEmployee;
