import React from 'react';
import TaskItem from "./TaskItem";

const TaskList = ({tasks, remove}) => {
    return (
        <div>
            <h1 style={{textAlign: "center"}}>Список задач</h1>
            {tasks.map(task =>
                <TaskItem task={task} key={task.id}/>
            )}
        </div>
    );
};

export default TaskList;
