import React, {useEffect, useState} from 'react';
import './styles/App.css';
import TaskList from "./components/TaskList";
import TaskForm from "./components/TaskForm";
import MyModal from "./components/UI/MyModal/MyModal";
import MyButton from "./components/UI/button/MyButton";
import TaskService from "./API/TaskService";
import Header from "./components/Header";
import {BrowserRouter as Router, Route, Routes} from "react-router-dom";

function App() {
    const [tasks, setTasks] = useState([]);
    const [modal, setModal] = useState(false);
    const [removeButton, setRemoveButton] = useState(false);

    useEffect(() => {
        fetchTasks();
    }, [modal, removeButton])

    const createTask = async (newTask) => {
        await TaskService.create(newTask);
        setModal(false);
    }

    async function fetchTasks() {
        const tasks = await TaskService.getAll();
        setTasks(tasks);
    }

    const removeTask = async (task) => {
        await TaskService.remove(task._links.self.href);
        setRemoveButton(false);
    }

  return (
    <div>
        <Header/>
        <div className="App">
            <MyButton style={{marginTop: 30}} onClick={() => setModal(true)}>
                Добавить задачу
            </MyButton>
            <MyModal visible={modal} setVisible={setModal}>
                <TaskForm create={createTask}/>
            </MyModal>
            <TaskList remove={removeTask} tasks={tasks} removeButton={removeButton} setRemoveButton={setRemoveButton}/>
        </div>
        <Router>
            <Routes>
                <Route path="/" element={<App />}/>
                <Route exact path="/request" element={<App/>}/>
                <Route exact path="/new-employee" element={<App/>}/>
                <Route exact path="/new-manager" element={<App/>}/>
            </Routes>
        </Router>
    </div>
  );
}

export default App;
