import React, {useEffect, useState} from 'react';
import './styles/App.css';
import TaskList from "./components/TaskList";
import TaskForm from "./components/TaskForm";
import MyModal from "./components/UI/MyModal/MyModal";
import MyButton from "./components/UI/button/MyButton";
import TaskService from "./API/TaskService";
import {Container, Nav, Navbar} from "react-bootstrap";

function App() {
    const [tasks, setTasks] = useState([]);
    const [modal, setModal] = useState(false);
    const [removeButton, setRemoveButton] = useState(false);

    useEffect(() => {
        fetchTasks();
    }, [modal, removeButton])

    const createTask = async (newTask) => {
        await TaskService.create(newTask);
        setModal(false);
    }

    async function fetchTasks() {
        const tasks = await TaskService.getAll();
        setTasks(tasks);
    }

    const removeTask = async (task) => {
        await TaskService.remove(task._links.self.href);
        setRemoveButton(false);
    }

  return (
    <div className="App">
        <Navbar collapseOnSelect expand="md" bg="dark" variant="dark">
            <Container>
                 <Navbar.Toggle aria-controls="responsive-navbar-nav"/>
                 <Navbar.Collapse id="responsive-navbar-nav">
                     <Nav className="mr-auto">
                         <Nav.Link href="/">Главная</Nav.Link>
                         <Nav.Link href="/request">Запросы</Nav.Link>
                         <Nav.Link href="/new-employee">Регистрация нового сотрудника</Nav.Link>
                         <Nav.Link href="/new-manager">Регистрация нового менеджера</Nav.Link>
                     </Nav>
                 </Navbar.Collapse>
            </Container>

        </Navbar>
        <MyButton style={{marginTop: 30}} onClick={() => setModal(true)}>
            Добавить задачу
        </MyButton>
        <MyModal visible={modal} setVisible={setModal}>
            <TaskForm create={createTask}/>
        </MyModal>
        <TaskList remove={removeTask} tasks={tasks} removeButton={removeButton} setRemoveButton={setRemoveButton}/>
    </div>
  );
}

export default App;
